mapper.select:
select l.lnum,l.name l_name ,c.name c_name,d.dongname ,count(case when m.mcode='M01' then 1 end) as gateway, count(case when m.mcode='M02' then 1 end) as activesensor, count(case when m.mcode='M03' then 1 end)as doorsensor, count(case when m.mcode='M04' then 1 end) as firesensor from local l, center c, machine m, dong d where l.lnum=m.lnum and c.cnum=m.cnum and d.dongcode = c.dongcode
and l.name=#{gu}
and dongname=#{dong}
and m.mstatus = '재고' group by c.name, l.name, l.lnum, d.dongname order by l.lnum

select count(*) from ( select l.lnum,l.name l_name ,c.name c_name,d.dongname ,count(case when m.mcode='M01' then 1 end) as gateway, count(case when m.mcode='M02' then 1 end) as activesensor, count(case when m.mcode='M03' then 1 end)as doorsensor, count(case when m.mcode='M04' then 1 end) as firesensor from local l, center c, machine m, dong d where l.lnum=m.lnum and c.cnum=m.cnum and d.dongcode = c.dongcode
and l.name=#{gu}
and dongname=#{dong}
and m.mstatus = '재고' group by c.name, l.name, l.lnum, d.dongname order by l.lnum )
